import { createStore } from 'vuex'

export default createStore({
  state: {
    token: null, // Estado inicial do token
  },
  mutations: {
    setToken(state, token) {
      state.token = token; // Atualiza o estado do token
    },
  },
  actions: {
    saveToken({ commit }, token) {
      commit('setToken', token); // Chama a mutation para atualizar o estado
    },
  },
  getters: {
    isAuthenticated: (state) => !!state.token, // Retorna true se o token estiver presente
  },
})
