import { createRouter, createWebHistory } from 'vue-router';
import Home from '../views/home.vue';

const routes = [
  {
    path: '/',
    name: 'home',
    component: Home,
  },
  {
    path: '/dashboard',
    name: 'dashboard',
    component: () => import('../views/DashboardView.vue'), // Página protegida
    meta: { requiresAuth: true }, // Define como protegida
  },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

// Middleware para verificar autenticação
router.beforeEach((to, from, next) => {
  const token = localStorage.getItem('token'); // Verifica se há token armazenado
  const isAuthenticated = !!token;

  if (to.meta.requiresAuth && !isAuthenticated) {
    // Se a rota requer autenticação e o usuário não está autenticado
    next('/'); // Redireciona para a página de login
  } else if (to.name === 'home' && isAuthenticated) {
    // Se o usuário já está autenticado e tenta acessar a página de login
    next('/dashboard'); // Redireciona para o dashboard
  } else {
    next(); // Continua para a rota desejada
  }
});

export default router;
